import React, {useState, useRef} from 'react';

function InputSample(){
    const [inputs, setInputs] = useState({
       name:'',
       nickname:''
    });
    const nameInput = useRef();
    const {name, nickname} = inputs;
    const onChange = e =>{
        const {value, name} = e.target;
        setInputs({
           ...inputs,
           [name]:value
        }); 
    };
    const onReset = () =>{
        setInputs({
           name:'',
           nickname:''
        });
        nameInput.current.focus();
    }
    return (
        <div>
            <input
             name="name"
             placeholder="이름"
             onChange={onChange}
            />
        </div>
    )
}
